import { Button, IconButton } from "@mui/material";
import RemoveCircleOutlineIcon from '@mui/icons-material/RemoveCircleOutline';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import React, { useState } from "react"; // Import thêm useState từ React
import { useDispatch } from "react-redux";
import { removeCartItem, updateCartItem } from "../../../State/Cart/Action";

const CartItem = ({ item }) => {
    const dispatch = useDispatch();
    const [quantity, setQuantity] = useState(item.quantity); // Sử dụng useState để lưu trữ số lượng sản phẩm

    const handleUpdateCartItem = (num) => {
        const updatedQuantity = quantity + num;
        if (updatedQuantity >= 1) { // Đảm bảo số lượng không nhỏ hơn 1
            setQuantity(updatedQuantity); // Cập nhật số lượng mới
            const data = { data: { quantity: updatedQuantity }, cartItemID: item?.id };
            dispatch(updateCartItem(data));
        }
    };

    const handleRemoveCartItem = () => {
        dispatch(removeCartItem(item.id));
    };

    return (
        <div className="p-5 shadow-lg border rounded-md">
            <div className="flex items-center">
                <div className="w-[5rem] h-[5rem] lg:w-[9rem] lg:h-[9rem]">
                    <img className="w-full object-cover object-top" src={item.product.imageUrl} alt="" />
                </div>
                <div className="ml-5 space-y-1">
                    <p className="font-semibold">{item.product.title}</p>
                    <p className="opacity-70">Số lượng: {quantity}</p> {/* Hiển thị số lượng từ state */}
                    <p className="opacity-70 mt-2">Shop: {item.product.brand}</p>
                    <div className='flex space-x-5 items-center text-gray-900 pt-6'>
                        <p className='font-semibold'>{item.price} VND</p>
                        <p className='opacity-50 line-through'> {item.discountedPrice} VND </p>
                        <p className='text-green-600 font-semibold'>{item.discountPersent}% Off</p>
                    </div>
                </div>
            </div>
            <div className="lg:flex items-center lg:space-x-10 pt-4">
                <div className="flex items-center space-x-1">
                    <IconButton onClick={() => handleUpdateCartItem(-1)} disabled={quantity <= 1}>
                        <RemoveCircleOutlineIcon />
                    </IconButton>
                    <span className="py-1 px-6 boder rounded-sm">{quantity}</span>
                    <IconButton onClick={() => handleUpdateCartItem(1)} sx={{ color: "RGB(145 85 253)" }}>
                        <AddCircleOutlineIcon />
                    </IconButton>
                </div>
                <div>
                    <Button onClick={handleRemoveCartItem} sx={{ color: "RGB(145 85 253)" }}>REMOVE</Button>
                </div>
            </div>
        </div>
    );
};

export default CartItem;
