import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { useParams, useSearchParams } from 'react-router-dom';
import { store } from '../../../State/Store';
import { updatePayment } from '../../../State/Payment/Action';
import { Alert, AlertTitle, Grid } from '@mui/material';
import OrderTraker from '../Order/OrderTraker';
import AddressCard from '../AddressCard/AddressCard';
import { getOrderById } from '../../../State/Order/Action';

const PaymentSuccess = () => {
    const [paymentId, setPaymentId] = useState();
    const [referenceId, setReferenceId] = useState();
    const [paymentStatus, setPaymentStatus] = useState();
    const { orderId } = useParams();

    const dispatch = useDispatch();
    const { order } = useSelector(store => store);

    console.log("order ", order.order)
    useEffect(() => {
        const urlParam = new URLSearchParams(window.location.search);

        setPaymentId(urlParam.get("razorpay_payment_id"))
        setPaymentStatus(urlParam.get("razorpay_payment_link_status"))
    }, [])

    useEffect(() => {
        if (paymentId) {
            const data = { orderId, paymentId }
            dispatch(getOrderById(orderId));
            dispatch(updatePayment(data))
        }


    }, [orderId, paymentId])

    return (
        <div className='px-2 lg:px-36'>

            <div className='flex flex-col justify-center items-center'>

                <Alert
                    variant='filled'
                    security='success'
                    sx={{ mb: 6, width: "fit-content" }}
                >
                    <AlertTitle>Payment Success</AlertTitle>
                    Xin chúc mừng Đơn hàng của bạn đã được đặt

                </Alert>
            </div>

            <OrderTraker activeStep={1} />

            <Grid container className='space-y-5 py-5 pt-20'>

                {order.order?.orderItems.map((item) => <Grid Grid container item className='shadow-xl rounded-md p-5'
                    sx={{ alignItems: "center", justifyContent: "space-between" }}
                >
                    <Grid item xs={6}>

                        <div className='flex items-center'>

                            <img className='w-[5rem] h-[5rem] object-cover object-top' src={item.product.imageUrl} alt='' />

                            <div className='ml-5 space-y-2'>
                                <p>{item.product.title}</p>
                                <div className='opacity-50 text-xs font-semibold space-x-5'>
                                    <span>Color: {item.color}</span>
                                    <span>Size: {item.size}</span>
                                </div>
                                <p>Seller : {item.product.brand}</p>
                                <p> {item.price} VND</p>

                            </div>
                        </div>

                    </Grid>
                    <Grid item>
                        <AddressCard address={order.order?.shippingAddress} />

                    </Grid>

                </Grid>)}

            </Grid>

        </div >
    )
}

export default PaymentSuccess
